import React from 'react';
import {makeStyles} from '@material-ui/styles';
import MostSellers from '../../Home/MostSellers/MostSellers';
const useStyles = makeStyles({
    offSection: {
        backgroundColor: '#36CE91',
        height: 360
    },
    offSectionContent: {
        display: 'flex',
        justifyContent: 'flex-start',
        flexDirection: 'row',
        padding: '30px 10px'
    },
    moreSection: {
        paddingRight: 20,
        paddingLeft: 8,
        display: "flex",
        flexDirection: "column",
        alignItems: "flex-start",
        justifyContent: "center"
    },
    imgSection: {
        width: 140
    }
});
const OffSection = () => {
    const classes = useStyles();
    return(
        <div className={classes.offSection}>
            <div className={classes.offSectionContent}>
                <div className={classes.moreSection}>
                    <img className={classes.imgSection} src="https://www.digikala.com/static/files/2e879398.png"/>
                    <span>مشاهده همه</span>
                </div>
                <MostSellers />
            </div>
        </div>
    )
}
export default OffSection;
