import React,{useState, useEffect} from 'react';
import {makeStyles} from '@material-ui/styles';
import CarouselDiscountItems from '../../../mainComponents/Carousel/CarouselDiscount';
import { useDispatch } from 'react-redux';
import axiosInstance from '../../../config/axios';
const useStyles = makeStyles({
    offSection: {
        backgroundColor: '#36CE91',
        height: 360
    },
    offSectionContent: {
        display: 'flex',
        justifyContent: 'flex-start',
        flexDirection: 'row',
        padding: '30px 10px'
    },
    moreSection: {
        paddingRight: 20,
        paddingLeft: 8,
        display: "flex",
        flexDirection: "column",
        alignItems: "flex-start",
        justifyContent: "center",
        marginTop: 20
    },
    imgSection: {
        width: 140
    },
    moreBtn: {
        fontSize: '0.875rem',
        color: 'white',
        fontWeight: 700,
        padding: '9px 0',
        lineHeight: '1.833'
    }
});

const OffSection = () => {
    const dispatch = useDispatch();
    const [mostSellers, setMostSellers] = useState({
        list: []
    });
    const [params, setParams] = useState({
        size: 10,
        start: 0,
        status: 'ACTIVE',
        // categoryId: 3,
        topselles: true
    });
    const getMostSellers = async () => {
        try {
            dispatch({loading: true, type: 'SHOW_LOADING'});
            const response = await axiosInstance.get('/products?start=0&size=10')
            debugger
            if(response.data)
            setMostSellers({
                ...mostSellers,
                list: response.data
            });
            dispatch({loading: false, type: 'SHOW_LOADING'});

        } catch (error) {
            dispatch({loading: false, type: 'SHOW_LOADING'});

        }
    }
    useEffect(() => {
        debugger
        getMostSellers();
    },[])
    const classes = useStyles();
    return(
        <div className={classes.offSection}>
            <div className={classes.offSectionContent}>
                <div className={classes.moreSection}>
                    <img className={classes.imgSection} src="https://www.digikala.com/static/files/2e879398.png"/>
                    <span className={classes.moreBtn}>مشاهده همه</span>
                </div>
                <CarouselDiscountItems data = {mostSellers.list} />
            </div>
        </div>
    )
}
export default OffSection;
