// @flow
import React, {useState, useEffect} from 'react';
import injectSheet from 'react-jss';
import {Switch, Route, Link } from 'react-router-dom';
import globalStyles from '../globalStyles';
import Footer from '../components/Footer/Footer';
import routes from '../config/routes';
import orderReq from '../requests/oredrs';
import {useDispatch, useSelector} from 'react-redux';
import CircularProgress from '@material-ui/core/CircularProgress';


const styles = theme => ({
  ...globalStyles,
  container: {
    maxWidth: 1000,
    margin: [[0, 'auto']],
  },
    blur: {
      filter: "blur(8px)"
    },
    loading: {
      width: 40,
      height: 40,
      position: "fixed",
      top: '40%',
      right: '45%'
    },
  header: {
    top: 0,
    width: '100%',
    position: 'fixed',
    left: 0,
    backgroundColor: 'rgba(255,255,255,0.95)',
    zIndex: 1000
  },
  headerWrapper: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: 50,
  },
  logo: {
    width: 45,
    height: 45,
  },
  menuButton: {
    border: 0,
    backgroundColor: 'transparent',
    cursor: 'pointer',
    padding: [[0, 18]],
  },
  menuIcon: {
    width: 20,
    height: 20,
  },
  leftHeader: {
    display: 'flex',
    padding: [[15, 0, 15, 0]],
    flexGrow: 1,
    textAlign: 'center',
    maxHeight: 50,
  },
  github: {
    display: 'flex',
    padding: [[15, 18]],
    flexGrow: 1,
    textAlign: 'center',
    maxHeight: 50,
    justifyContent: 'center',
  },
  githubIcon: {
    height: '1em',
    marginRight: 8,
    display: 'inline-block',
  },
  githubTitle: {
    color: theme.baseColor,
    padding: '0 15px'
  },
  content: {
    marginTop: 45,
    backgroundColor: '#F5F5F5',
    marginBottom: 55
  },
  footer: {
    width: '100%',
  },
  footerWrapper: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: 80,
    padding: [[0, 18]],
    color: theme.textMuted,
    fontSize: 14,
    borderTop: '1px solid rgba(0,0,0,.15)',
    '@media (min-width: 860px)': {
      padding: 0,
    },
    '& a': {
      color: theme.textMuted,
    },
  },
});

const App = (props) => {
  const dispatch = useDispatch();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const loading = useSelector(state => state.loading)
  const [location, setLocation] = useState({
    lat: 0,
    lon: 0
  })
  const fetchOrders = async () => {
    const response = await orderReq('GET', null, null)
    dispatch({orders: response.data, type: 'SET_ORDERS'})
  }


  useEffect(() => {

    fetchOrders();
    const elem = document.getElementById('startingLoader');
    window.onload = () => {
      if (elem) {
        elem.remove();
      }
    };
  },[])

  const onSetSidebarOpen = (open) => {
    setSidebarOpen(open);
  }

  const handleCallbackCloseSidebar = (newState) => {
    setSidebarOpen(!newState);
  }

  const menuButtonClick = () => {
    onSetSidebarOpen(sidebarOpen);
  }
    const { classes } = props;



    return (
      <React.Fragment>
        <div className={loading ? classes.blur : ''}>

<div className="App">

    <header className={classes.header}>
      <div className={classes.container}>
        <div className={classes.headerWrapper}>
          <div className={classes.leftHeader}>

            <Link to="/">
              <svg className={classes.logo} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 841.9 595.3">
